import React from 'react';
import './ToDoListMat.css';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import { useState } from 'react';
import ListComp from './ListComp';

const ToDoListMat = () => {
    const [item, setItem] = useState('Buy an Apple');
    const [itemArr,setItemArr] = useState([]);
    const inputEvent = (event) => {
        setItem(event.target.value);
    };
    console.log(setItemArr);
    const itemsList = () => {
        setItemArr((preVal)=>{
         return [...preVal,item]  
        });
    };
    return (
        <>
            <div className="main_div">
                <div className="center_div">
                    <br />
                    <h1>ToDo List</h1>
                    <br />
                    <input type="text" placeholder="Add Items" onChange={inputEvent} />
                    <Button className="newBtn" onClick={itemsList}>
                        <AddIcon />
                    </Button>
                    <br />
                    <ol>
                        {itemArr.map((val) => {
                            return <ListComp value = {val} />
                        })}
                    </ol>

                </div>
            </div>
        </>
    );
};

export default ToDoListMat;
